sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    if (current.add_new_vendor == true) 
	{

        if (current.vendor == '')
		{
            //if vendor is a null string set to the
            //lookup value of current.item_description_lookup
            myObj.setValue('vendor', current.vendor_lookup);
        } 
		else 
		{	
			var myObj = new GlideRecord('x_561190_micro_p_0_vendor');
			myObj.addQuery('vendor', current.vendor);
			myObj.query();

            //if the vendor record does not already exist,
            //insert a new one for the vendorn 
            if (myObj.next())
			{
                myObj.setValue('add_new_vendor', false);
            } 
			else
			{				
                myObj.setValue('vendor', current.vendor);
                myObj.setValue('sys_created_by', current.sys_created_by);
                myObj.setValue('sys_created_on', current.sys_created_on);
                //insert record
                myObj.insert();
            }
        }
    }

})(current, previous);
