sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

   var grUpdOpeningBalance = new GlideRecord('x_561190_micro_p_0_fy_opening_balance');	
   var canWrite = grUpdOpeningBalance.canWrite(); // used to check if curent user has write rights

   grUpdOpeningBalance.addQuery('fiscal_year_lookup', current.fiscal_yr_lookup);
   grUpdOpeningBalance.addQuery('account_lookup', current.account_number_lookup);
   grUpdOpeningBalance.addQuery('division_lookup', current.organization_ref);	
   grUpdOpeningBalance.query();  // query with select conditions specified by the three addQuery

	if (canWrite)
	{
		while (grUpdOpeningBalance.next()) { // use loop to update record(s) erturned by query			
			var previous_final_cost_non_legacy = parseFloat(previous.final_cost_non_legacy);		
			var current_final_cost_non_legacy = parseFloat(current.final_cost_non_legacy);			
			var diff = parseFloat(previous_final_cost_non_legacy - current_final_cost_non_legacy);		
			var remaining_balance = parseFloat(grUpdOpeningBalance.getValue('remaining_balance'));		
			var final_remaining_balance = Math.round((remaining_balance  + diff) * 100)/100; //this will round specified dollar amounts to two-place decimal

			grUpdOpeningBalance.setValue('remaining_balance', final_remaining_balance); // set remaining balance
			grUpdOpeningBalance.update(); // performs update
		}
    }
})(current, previous);
