sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    if (current.add_new_item_description == true) {   

		if (current.item_description == '')
		{
			//if item_description is a null string set to the
			//lookpu value of current.item_description_lookup
			myObj.setValue('item_description', current.item_description_lookup);
		}
		else
		{		
			var myObj = new GlideRecord('x_561190_micro_p_0_item_description');		
			myObj.addQuery('item_description', current.item_description);
			myObj.query();

			//if the item decription record does not already exist,
			//insert a new one for the item decription 
			if (myObj.next())
			{
			myObj.setValue('add_new_item_description', false);
			}
			else
			{		
				myObj.setValue('item_description', current.item_description);
				myObj.setValue('sys_created_by', current.sys_created_by);
				myObj.setValue('sys_created_on', current.sys_created_on);
				//insert record
				myObj.insert();
			}
		}
    }

})(current, previous);
