sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) 
{
    if (isLoading || newValue === '') 
	{
        return;
    }
    if (newValue)
	{
        var status_lookup = g_form.getReference('status_lookup', popStatusLookupInfo);
    }

    function popStatusLookupInfo(status_lookup)
	{
		//v is the name of the referenced field in the Status table
		var order_status = g_form.getValue('order_status');

		if (order_status == '')
		{
			g_form.setValue('order_status', status_lookup.status);
		}
	}
}
