sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (newValue) {
        var account_number_lookup = g_form.getReference('account_number_lookup', popAccountNumberLookupInfo);
    }

    function popAccountNumberLookupInfo(account_number_lookup) {
        //account_number is the name of the referenced field in the Accounts table

        var account_number = g_form.getValue('account_number');

        if (account_number == '')
		{
            g_form.setValue('account_number', account_number_lookup.account_number);
        }
    }
}
