sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (newValue) {
        //organization_ref is the Refrence field for the Approval Official Division Lookup
		//in the Transactions table
        var organization_ref = g_form.getReference('organization_ref', popAppOfficialDivInfo);
    }

    function popAppOfficialDivInfo(organization_ref) {
        //name is the referenced field in the Organizations table
		var approve_official_division = g_form.getValue('approve_official_division');

		if (approve_official_division == '')
		{
			g_form.setValue('approve_official_division', organization_ref.name);
		}
    }
}
