sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (newValue) {
        //fiscal_year_lookup is the name of the Reference field for Fiscal Year
        //in the Tansaction table.
        var fiscal_yr_lookup = g_form.getReference('fiscal_yr_lookup', popFiscalYrInfo);
    }

    function popFiscalYrInfo(fiscal_yr_lookup) {
        //fiscal_year is the name of field in the Fiscal Year table, 
        //and hence is referenced as such with dot notation
        var fiscal_year = g_form.getValue('fiscal_year');

        if (fiscal_year == '')
		{
            g_form.setValue('fiscal_year', fiscal_yr_lookup.fiscal_year);
		}
    }
}
