sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (newValue) {
        //item_description_lookup is the reference field for Item Description Lookup in the Transactions table
        var item_description_lookup = g_form.getReference('item_description_lookup', popItemDescription);
    }

    function popItemDescription(item_description_lookup) {
        //item_description is the name of field in the Item Description table, 
        //and hences is referenced with dot notation
        var item_description = g_form.getValue('item_description');

        if (item_description == '') {
            g_form.setValue('item_description', item_description_lookup.item_description);
        }

    }
}
