sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (newValue) {
        //vendor_lookup is the Reference field for the Vendor Lookupp  in the Transactions table
        var vendor_lookup = g_form.getReference('vendor_lookup', popVendorInfo);
    }

    function popVendorInfo(vendor_lookup) {

            //vendor is the name of field in the Vendor table, 
            //and hences is referenced with dot notation
            var vendor = g_form.getValue('vendor');

			if (vendor == '')
			{
				g_form.setValue('vendor', vendor_lookup.vendor);
			}
        }
    }
