sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (newValue) {
        //vendor_lookup is the Reference field for the Vendor Lookupp  in the Transactions table
        var credit_card_holder = g_form.getReference('credit_card_holder', popCreditCardHolderInfo);
    }

    function popCreditCardHolderInfo(credit_card_holder) {
        //credit_card_holder is the name of the field, 
        //and hences is referenced with dot notation
        //alert(credit_card_holder.last_name);

        var cc_holder_name = g_form.getValue('cc_holder_name');

        if (cc_holder_name == '') {
            g_form.setValue('cc_holder_name', credit_card_holder.first_name + ' ' + credit_card_holder.last_name);
        }

    }
}
