sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (newValue) {
        //vendor_lookup is the Reference field for the Vendor Lookupp  in the Transactions table
        var approval_official_lookup = g_form.getReference('approval_official_lookup', popApprovalOfficialLookupInfo);
    }

    function popApprovalOfficialLookupInfo(approval_official_lookup) {
        //approval_official_lookup is the name of the field, 
        //and hence is referenced with dot notation
        var approve_official = g_form.getValue('approve_official');

        if (approve_official == '') {
            g_form.setValue('approve_official', approval_official_lookup.first_name + ' ' +
                approval_official_lookup.last_name);
        }

    }
}
