sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    if (newValue) {
        //item_description_lookup is the reference field for Item Description Lookup in the Transactions table
		var account_lookup = g_form.getReference('account_lookup', popAccountLookup);
        //var division_lookup = g_form.getReference('division_lookup', popDivisionLookup);
    }

    function popAccountLookup(account_lookup) {
        //item_description is the name of field in the Item Description table, 
        //and hences is referenced with dot notation

         g_form.setValue('acct_no', account_lookup.account_number);
    }
}
